<?php
session_start();
require_once "db.php";

if (!isset($_SESSION['staff'])) {
    die("Access denied.");
}

$staff_id = $_SESSION['staff'];

// Fetch staff + biodata + appraisal
$staff = $conn->query("SELECT s.*, b.*, a.* FROM staff s 
                       LEFT JOIN save_biodata b ON s.staff_id = b.staff_id
                       LEFT JOIN academic_appraisal a ON s.staff_id = a.staff_id
                       WHERE s.staff_id = '$staff_id'")->fetch_assoc();

if (!$staff) die("No data found.");

$passport = $staff['passport'] && file_exists($staff['passport']) ? $staff['passport'] : 'uploads/default.png';
?>

